mapper.update:
update
set
    tt.tag_name = #{tagName}
    tt.update_date = #{updateDate}
    um.username = #{username}
from
    tour_tag tt
    left outer join tour_category tc on (tt.category_id = tc.category_id)
    left outer join user_mst um on (tt.user_id = um.user_id)
where
    tt.tag_id = #{tagId}
