mapper.select:
select count(*) from user_mst um left outer join role_dtl rd on (rd.user_id = um.user_id) left outer join role_mst rm on (rm.role_id = rd.role_id) where 1 = 1
and ( um.username like CONCAT('%', #{searchValue}, '%') or um.name like CONCAT('%', #{searchValue}, '%') or rm.role_name like CONCAT('%', #{searchValue}, '%') )
and role_name = #{roleName}

select count(*) from role_mst where 1 = 1
and ( role_id like CONCAT('%', #{searchValue}, '%') or role_name like CONCAT('%', #{searchValue}, '%') )
and role_id = #{roleId}
and role_name = #{roleName}

select um.user_id as userId, um.username, um.name,
rd.user_id as userId, rd.role_id as roleId,
rm.role_id as roleId, rm.role_name as roleName from user_mst um left outer join role_dtl rd on(rd.user_id = um.user_id) left outer join role_mst rm on(rm.role_id = rd.role_id) where 1 = 1
and ( um.username like CONCAT('%', #{searchValue}, '%') or um.name like CONCAT('%', #{searchValue}, '%') or rm.role_name like CONCAT('%', #{searchValue}, '%') )
and um.username = #{username}
and um.name = #{name}
and rm.role_name = #{roleName}
order by
um.username, um.name, rm.role_name
um.user_id
limit #{index}, #{count}

SELECT role_id as roleId, role_name as roleName from role_mst where 1 = 1
and ( role_id like CONCAT('%', #{searchValue}, '%') or role_name like CONCAT('%', #{searchValue}, '%') )
and role_id = #{roleId}
and role_name = #{roleName}
order by
role_id, role_name
role_id
limit #{index}, #{count}

SELECT um.user_id as userId, um.username, um.name,
rd.user_id as userId,
rm.role_id as roleId, rm.role_name as roleName from user_mst um left outer join role_dtl rd on (rd.user_id = um.user_id) left outer join role_mst rm on (rm.role_id = rd.role_id) where um.username = #{username};

SELECT um.user_id as userId, um.username, um.name,
rd.user_id as userId,
rm.role_id as roleId, rm.role_name as roleName from user_mst um left outer join role_dtl rd on (rd.user_id = um.user_id) left outer join role_mst rm on (rm.role_id = rd.role_id) where um.name = #{name};

SELECT um.user_id as userId, um.username, um.name,
rd.user_id as userId,
rm.role_id as roleId, rm.role_name as roleName from user_mst um left outer join role_dtl rd on (rd.user_id = um.user_id) left outer join role_mst rm on (rm.role_id = rd.role_id) where um.user_id = #{userId};

SELECT um.user_id as userId, um.name,
rd.user_id as userId, rd.role_id as roleId,
rm.role_id as roleId, rm.role_name as roleName from role_mst rm left outer join role_dtl rd on(rd.role_id = rm.role_id) left outer join user_mst um on(um.user_id = rd.user_id) where um.user_id = #{userId};

mapper.update:
update (select um.user_id as userId, um.username, um.name,
rd.user_id as userId,
rm.role_id as roleId, rm.role_name as roleName from role_mst rm left outer join role_dtl rd on (rd.role_id = rm.role_id) left outer join user_mst um on (um.user_id = rd.user_id) ) set rm.role_name = #{roleName} where um.username = #{username}

update (select um.user_id as userId, um.username, um.name,
rd.user_id as userId,
rm.role_id as roleId, rm.role_name as roleName from role_mst rm left outer join role_dtl rd on (rd.role_id = rm.role_id) left outer join user_mst um on (um.user_id = rd.user_id) ) set rm.role_name = #{roleName} where um.name = #{name}
